import CustomNavBar from '../components/Navbar';
import React, { useEffect } from 'react';
import { checkAuthenticated } from '../actions/auth';
import { connect } from 'react-redux';
import { Navigate, useNavigate, useParams } from 'react-router-dom';
import CSRFToken from '../components/CSRFToken';

const Dashboard = ({ checkAuthenticated, isAuthenticated }) => {
    const { username } = useParams(); // Odczytaj parametr "username" z adresu URL
    const navigate = useNavigate(); // Hook do przekierowywania

    useEffect(() => {
        checkAuthenticated();
    }, []);

    const onSubmit = async (e) => {
        e.preventDefault();
        // Możesz tutaj obsługiwać formularz
    };

    // Sprawdź, czy użytkownik jest zalogowany, jeśli nie, przekieruj go do strony logowania
    if (!isAuthenticated) {
        navigate('/login');
        return null;
    }

    return (
        <div className='container mt-5'>
            <CustomNavBar />
            <CSRFToken />
            <h1>Jesteś zalogowany jako: {username}</h1>
        </div>
    );
};

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
});

export default connect(mapStateToProps, { checkAuthenticated })(Dashboard);
