import React, { useState, createContext } from 'react';
import { Navigate, Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { login } from '../actions/auth';
import CSRFToken from '../components/CSRFToken';
import CustomNavBar from '../components/Navbar';

const UserContext = createContext();

const Login = ({ login, isAuthenticated }) => {
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });

    const { username, password } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();

        // Wywołaj metodę saveUsernameToContext
        saveUsernameToContext();

        login(username, password);
    };

    if (isAuthenticated)
        return <Navigate to="/dashboard" />;

    return (
        <div className="container mt-5">
            <CustomNavBar />
            <h1>Sign In</h1>
            <p>Sign into your Session Auth account</p>
            <form onSubmit={e => onSubmit(e)}>
                <CSRFToken />
                <div className="form-group">
                    <label className="form-label">Username: </label>
                    <input
                        className="form-control"
                        type="text"
                        placeholder="Username*"
                        name="username"
                        onChange={e => onChange(e)}
                        value={username}
                        required
                    />
                </div>
                <div className="form-group">
                    <label className="form-label mt-3">Password: </label>
                    <input
                        className="form-control"
                        type="password"
                        placeholder="Password*"
                        name="password"
                        onChange={e => onChange(e)}
                        value={password}
                        minLength="6"
                        required
                    />
                </div>
                <button className="btn btn-primary mt-3" type="submit">
                    Login
                </button>
            </form>
            <p className="mt-3">
                Don't have an Account? <Link to="/register">Sign Up</Link>
            </p>
        </div>
    );
};

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated
});

export const UserContextProvider = ({ children }) => {
    const [username, setUsername] = useState(''); // Definiuj username jako stan wewnątrz UserContextProvider

    const saveUsernameToContext = (newUsername) => {
        setUsername(newUsername);
    };

    return (
        <UserContext.Provider value={{ username, saveUsernameToContext }}> {/* Przekazuj username i funkcję saveUsernameToContext */}
            {children}
        </UserContext.Provider>
    );
};

export default connect(mapStateToProps, { login })(Login);
