import CustomNavBar from '../components/Navbar';
import React, { useEffect, useState, useContext } from 'react';
import { checkAuthenticated } from '../actions/auth';
import { connect } from 'react-redux';
import { load_user } from '../actions/profile';
import { Navigate } from 'react-router-dom';
import CSRFToken from '../components/CSRFToken';
import { UserContext } from '../UserContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = ({ checkAuthenticated, load_user, isAuthenticated }) => {
    const navigate = useNavigate();
    const [navigationCompleted, setNavigationCompleted] = useState(false);
    useEffect(() => {
        checkAuthenticated();
        load_user();
    }, []);

    const { user, setUser } = useContext(UserContext);

    const [documentData, setDocumentData] = useState({
        topic: '',
        purpose: ''
    });
    const { topic, purpose } = documentData;
    

    const onChange = e => setDocumentData({ ...documentData, [e.target.name]: e.target.value });

    const onSubmit = async e => {
        e.preventDefault();
        
        
        await setUser({ ...user, purpose, topic });

        try {
            const queryParams = `topic=${encodeURIComponent(topic)}&purpose=${encodeURIComponent(purpose)}`;
            navigate(`/dashboard${queryParams}`);    
            
            setNavigationCompleted(true);
            
        } catch (error) {
            console.error(error);
        }
        
    };
    useEffect(() => {
        if (navigationCompleted) {
            const queryParams = `topic=${encodeURIComponent(topic)}&purpose=${encodeURIComponent(purpose)}`;
            // Execute the Axios request here
            axios.get(`/languagemodel/run_generate`, { params: queryParams })
                .then((response) => {
                    // Handle the response here
                    console.log(response);
                })
                .catch((error) => {
                    console.error(error);
                });
        }
    }, [navigationCompleted]);

    
    

    return isAuthenticated ? (
        <div className='container mt-5'>
            <CustomNavBar />
            <h1>Jesteś zalogowany</h1>
            <p>Wprowadź temat oraz cel pracy</p>
            <form onSubmit={e => onSubmit(e)}>
                <CSRFToken />
                <div className='form-group'>
                    <label className='form-label'>topic: </label>
                    <input
                        className='form-control'
                        type='text'
                        placeholder='Temat'
                        name='topic'
                        onChange={e => onChange(e)}
                        value={topic}
                        minLength='15'
                        required
                    />
                </div>
                <div className='form-group'>
                    <label className='form-label mt-3'>purpose: </label>
                    <input
                        className='form-control'
                        type='purpose'
                        placeholder='Cel'
                        name='purpose'
                        onChange={e => onChange(e)}
                        value={purpose}
                        minLength='15'
                        required
                    />
                </div>
                <button className='btn btn-primary mt-3' type='submit'>Generate !</button>
            </form>
        </div>
    ) : (
        <Navigate to="/login" />
    );
};

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps, { checkAuthenticated, load_user })(Dashboard);



Dashboard.contextType = UserContext;
