import CustomNavBar from '../components/Navbar';
import React, { useEffect, useState, useContext} from 'react';
import { checkAuthenticated } from '../actions/auth';
import { connect } from 'react-redux';
import { load_user } from '../actions/profile';
import { Navigate } from 'react-router-dom';
import CSRFToken from '../components/CSRFToken';
import { UserContext } from '../UserContext';

const Dashboard = ({checkAuthenticated, load_user,isAuthenticated}) => {
    useEffect(() => {
        checkAuthenticated();
        load_user();
    }, []);
    const { user, setUser } = useContext(UserContext);

    const [documentData, setdocumentData] = useState({
        topic: '',
        purpose: ''
    });
    const { topic, purpose } = documentData;

    const onChange = e => setdocumentData({ ...documentData, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();
        setUser({ ...user, purpose, topic });
        console.log(user);
    };


    return isAuthenticated ? (
        <div className='container mt-5'>
            <CustomNavBar/>
            <h1>Jesteś zalogowany</h1>
            <p>Wprowadź temat oraz cel pracy</p>
            <form onSubmit={e => onSubmit(e)}>
                <CSRFToken />
                <div className='form-group'>
                    <label className='form-label'>topic: </label>
                    <input
                        className='form-control'
                        type='text'
                        placeholder='Temat'
                        name='topic'
                        onChange={e => onChange(e)}
                        value={topic}
                        minLength='15'
                        required
                    />
                </div>
                <div className='form-group'>
                    <label className='form-label mt-3'>purpose: </label>
                    <input
                        className='form-control'
                        type='purpose'
                        placeholder='Cel'
                        name='purpose'
                        onChange={e => onChange(e)}
                        value={purpose}
                        minLength='15'
                        required
                    />
                </div>
                <button className='btn btn-primary mt-3' type='submit'>Generate !</button>
            </form>
            
        </div>
      ) : (
        <Navigate to="/login" />
      );
    
    
};
const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated
});
export default connect(mapStateToProps, { checkAuthenticated, load_user })(Dashboard);

Dashboard.contextType = UserContext;
