import CustomNavBar from '../components/Navbar';
import React, { useEffect, useState, useContext } from 'react';
import { checkAuthenticated } from '../actions/auth';
import { connect } from 'react-redux';
import { load_user } from '../actions/profile';
import { Navigate } from 'react-router-dom';
import CSRFToken from '../components/CSRFToken';
import { UserContext } from '../UserContext';
import axios from 'axios';

const Dashboard = ({ checkAuthenticated, load_user, isAuthenticated }) => {
    useEffect(() => {
        checkAuthenticated();
        load_user();
    }, []);

    const { user, setUser } = useContext(UserContext);

    const [documentData, setDocumentData] = useState({
        topic: '',
        purpose: ''
    });
    const { topic, purpose } = documentData;
    const [isUpdatingUser, setIsUpdatingUser] = useState(false);

    const onChange = e => setDocumentData({ ...documentData, [e.target.name]: e.target.value });

    const onSubmit = async e => {
        e.preventDefault();
        setIsUpdatingUser(true);

        await setUser({ ...user, purpose, topic });

        setIsUpdatingUser(false);
    };

    // Wykonaj console.log(user) tylko jeśli isUpdatingUser jest false
    if (!isUpdatingUser) {
        console.log(user);
        axios.post('http://127.0.0.1:8000/languagemodel/querydata', user, {
      })
      .then((response) => {
        console.log('succces')
      })
      .catch((error) => {
        window.alert('Generate nieudany w frontend');
        console.error('Error:', error);
      });
    }

    return isAuthenticated ? (
        <div className='container mt-5'>
            <CustomNavBar />
            <h1>Jesteś zalogowany</h1>
            <p>Wprowadź temat oraz cel pracy</p>
            <form onSubmit={e => onSubmit(e)}>
                <CSRFToken />
                <div className='form-group'>
                    <label className='form-label'>topic: </label>
                    <input
                        className='form-control'
                        type='text'
                        placeholder='Temat'
                        name='topic'
                        onChange={e => onChange(e)}
                        value={topic}
                        minLength='15'
                        required
                    />
                </div>
                <div className='form-group'>
                    <label className='form-label mt-3'>purpose: </label>
                    <input
                        className='form-control'
                        type='purpose'
                        placeholder='Cel'
                        name='purpose'
                        onChange={e => onChange(e)}
                        value={purpose}
                        minLength='15'
                        required
                    />
                </div>
                <button className='btn btn-primary mt-3' type='submit' disabled={isUpdatingUser}>
                    {isUpdatingUser ? 'Aktualizowanie...' : 'Generate !'}
                </button>
            </form>
        </div>
    ) : (
        <Navigate to="/login" />
    );
};

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps, { checkAuthenticated, load_user })(Dashboard);



Dashboard.contextType = UserContext;
